<template>
	<view class="content">
		<image class="logo" src="/static/logo.png"></image>
		<view>
			<text class="title">{{title}}</text>
		</view>
		<button @click="toPage">跳转到我的页面</button>
		<min-a to="my">跳转到我的页面min-a标签跳转</min-a>
		<min-a :to="{name: 'my', query: {id: 123}}">跳转到我的页面min-a标签跳转带参数</min-a>
	</view>
</template>

<script>
	export default {
		data() {
			return {
				title: 'index',
			}
		},
		onLoad() {
			// 解析路由参数
			console.log(this.$parseURL())
			
			// 'name'不是以下划线开头的表示会缓存到Map中，在程序生命周期内有并且在有效时间内有效
			this.$cache.set('name', 'MinCache')

			// 过期时间设置为0表示不会过期
			// 注意：'test'并不是以下划线命名表示在程序生命周期永久缓存
			this.$cache.set('test', 'testdemo', 0)

			// 先检查有没有缓存
			// 没有缓存就发起请求
			// '_imgURL'是以下划线开头的表示会缓存到Storage
			if (!this.$cache.has('_imgURL')) {
				// 模拟请求1s后返回
				setTimeout(() => {
					// 模拟数据
					let data = 'data:image/png;base64,'

					// 过期时间设置为0表示不会过期
					// 注意：'_imgURL'是以下划线命名表示永久缓存到Storage
					this.$cache.set('_imgURL', data, 0)
				}, 1000)
			}

			// 缓存时间时设置的默认值
			this.$cache.set('delete', 'testdelete')

			// 给定缓存时间10s
			this.$cache.set('test_timeout', 'test_timeout', 10)
			console.log(this.$cache.get('test_timeout'))
			setTimeout(() => {
				console.log(this.$cache.get('test_timeout'))
			}, 11000)
			

			this.testRequest1()
			this.testRequest2()
		},
		methods: {
			toPage () {
				// 跳到my的页面  query是传递的参数
				this.$openPage({
					name: 'my',
					query: {id: 123}
				})
			},

			// 使用方法一
			testRequest1 () {
				this.$minApi.uniapp({wd: 'uni-app'}).then(res => {
					this.res = res
					console.log(res)
				}).catch(err => {
					console.log(err)
				})
			},

			// 使用方式二
			async testRequest2 () {
				try {
					const res = await this.$minApi.uniapp({wd: 'uni-app'})
					console.log(res)
				} catch (err) {
					console.log(err)
				}
			}
		}
	}
</script>

<style>
	.content {
		text-align: center;
		height: 400upx;
	}

	.logo {
		height: 200upx;
		width: 200upx;
		margin-top: 200upx;
	}

	.title {
		font-size: 36upx;
		color: #8f8f94;
	}
</style>
